CREATE TABLE `Favorite` (ID VARCHAR(50) NOT NULL, SeriesName VARCHAR(50), Genre VARCHAR(50), FirstAired VARCHAR(50), ContentRating VARCHAR(50), Overview TEXT, Network VARCHAR(50), Rating VARCHAR(50), Status VARCHAR(50), Runtime VARCHAR(50), Banner VARCHAR(50), IMDb_ID VARCHAR(50), PRIMARY KEY(ID));
CREATE TABLE `Episode`(Series VARCHAR(50) NOT NULL, ID VARCHAR(50) NOT NULL, SeasonNumber VARCHAR(50), EpisodeNumber VARCHAR(50), EpisodeName VARCHAR(50), FirstAired VARCHAR(50), Overview TEXT, ProductionCode VARCHAR(50), Rating VARCHAR(50), Image VARCHAR(50), IMDb_id VARCHAR(50), GuestStars TEXT, Writers TEXT, Directors TEXT, PRIMARY KEY(ID));
CREATE TABLE `Actor` (Series VARCHAR(50) NOT NULL, Name VARCHAR(50) NOT NULL, Pic VARCHAR(50), Role VARCHAR(50));